mapper.select:
SELECT basic_id,
        intype,
        fenmian,
        changhoublood,
        changruganran,
        staytime,
        money,
        changhouyiyi,
        chanchijilu,
        AD
        ,el_id
from ElseDiscrible


        and basic_id = #{basic_id}


        and intype = #{intype}


        and fenmian = #{fenmian}


        and changhoublood = #{changhoublood}


        and changruganran = #{changruganran}


        and staytime = #{staytime}


        and money = #{money}


        and changhouyiyi = #{changhouyiyi}


        and chanchijilu = #{chanchijilu}


        and AD = #{AD}


        and el_id = #{el_id}

mapper.update:
UPDATE ElseDiscrible


        , basic_id = #{basic_id}


        , intype = #{intype}


        , fenmian = #{fenmian}


        , changhoublood = #{changhoublood}


        , changruganran = #{changruganran}


        , staytime = #{staytime}


        , money = #{money}


        , changhouyiyi = #{changhouyiyi}


        , chanchijilu = #{chanchijilu}


        , AD = #{AD}
