mapper.select:
SELECT basic_id,
        k,
        na,
        cl,
        ca,
        p,
        co2,
        tp,
        alb,
        gb,
        whiteballratio,
        prealbumin,
        tba,
        tb,
        brd,
        urd,
        alt,
        got,
        alk,
        gpt,
        ldh,
        ce,
        glucose,
        bun,
        cr,
        ua,
        cystain,
        p_time
        ,pbg_id
from PregnantBigCheck


        and basic_id = #{basic_id}


        and k = #{k}


        and na = #{na}


        and cl = #{cl}


        and ca = #{ca}


        and p = #{p}


        and co2 = #{co2}


        and tp = #{tp}


        and alb = #{alb}


        and gb = #{gb}


        and whiteballratio = #{whiteballratio}


        and prealbumin = #{prealbumin}


        and tba = #{tba}


        and tb = #{tb}


        and brd = #{brd}


        and urd = #{urd}


        and alt = #{alt}


        and got = #{got}


        and alk = #{alk}


        and gpt = #{gpt}


        and ldh = #{ldh}


        and ce = #{ce}


        and glucose = #{glucose}


        and bun = #{bun}


        and cr = #{cr}


        and ua = #{ua}


        and cystain = #{cystain}


        and p_time = #{p_time}


        and pbg_id = #{pbg_id}

