mapper.select:
SELECT basic_id,
        wbc_number,
        rbc_number,
        hb_number,
        plt_number,
        neut_rate,
        ly_rate,
        mono_rate,
        egr_rate,
        bgr_rate,
        neut_number,
        ly_number,
        mono_number,
        egr_number,
        bgr_number,
        mcv,
        hct,
        mch,
        mchc,
        rdw_cv,
        rdw_sd,
        mpv,
        pct,
        pdw,
        plcr,
        r_time
        ,rbt_id
from RoutineBloodTest


        and basic_id = #{basic_id}


        and wbc_number = #{wbc_number}


        and rbc_number = #{rbc_number}


        and hb_number = #{hb_number}


        and plt_number = #{plt_number}


        and neut_rate = #{neut_rate}


        and ly_rate = #{ly_rate}


        and mono_rate = #{mono_rate}


        and egr_rate = #{egr_rate}


        and bgr_rate = #{bgr_rate}


        and neut_number = #{neut_number}


        and ly_number = #{ly_number}


        and mono_number = #{mono_number}


        and egr_number = #{egr_number}


        and bgr_number = #{bgr_number}


        and mcv = #{mcv}


        and hct = #{hct}


        and mch = #{mch}


        and mchc = #{mchc}


        and rdw_cv = #{rdw_cv}


        and rdw_sd = #{rdw_sd}


        and mpv = #{mpv}


        and pct = #{pct}


        and pdw = #{pdw}


        and plcr = #{plcr}


        and r_time = #{r_time}


        and rbt_id = #{rbt_id}

mapper.update:
UPDATE RoutineBloodTest


        , basic_id = #{basic_id}


        , wbc_number = #{wbc_number}


        , rbc_number = #{rbc_number}


        , hb_number = #{hb_number}


        , plt_number = #{plt_number}


        , neut_rate = #{neut_rate}


        , ly_rate = #{ly_rate}


        , mono_rate = #{mono_rate}


        , egr_rate = #{egr_rate}


        , bgr_rate = #{bgr_rate}


        , neut_number = #{neut_number}


        , ly_number = #{ly_number}


        , mono_number = #{mono_number}


        , egr_number = #{egr_number}


        , bgr_number = #{bgr_number}


        , mcv = #{mcv}


        , hct = #{hct}


        , mch = #{mch}


        , mchc = #{mchc}


        , rdw_cv = #{rdw_cv}


        , rdw_sd = #{rdw_sd}


        , mpv = #{mpv}


        , pct = #{pct}


        , pdw = #{pdw}


        , plcr = #{plcr}


        , r_time = #{r_time}
