mapper.select:
SELECT user_id,
        basic_id,
        page_num,
        finish
        ,task_id
from Task


        and user_id = #{user_id}


        and basic_id = #{basic_id}


        and page_num = #{page_num}


        and finish = #{finish}


        and task_id = #{task_id}

mapper.update:
UPDATE Task


        , user_id = #{user_id}


        , basic_id = #{basic_id}


        , page_num = #{page_num}


        , finish = #{finish}

